var Wsongs = [];

var temp = 1;

var rounds = 1;
var roundsend = 64;

let randnumber = Math.floor(Math.random() * (song.length));

var d45 = document.getElementById('d45');

var rounds_p = document.getElementById("rounds");
var d1 = document.getElementById('d1');
d1.src = song[randnumber];
song.splice(song.indexOf(d1.src),1);
temp++;
randnumber = Math.floor(Math.random() * (song.length));
var d2 = document.getElementById('d2');
d2.src = song[randnumber];
song.splice(song.indexOf(d2.src),1);
temp++;


function Next(n){
    
    if(rounds==roundsend){

        rounds=0;
        roundsend = roundsend/2;
        song = Wsongs;
        Wsongs = [];
        temp = 0;
        if(roundsend==0.5){

            EndGame();
            roundsend = 0;

        }
        d45.src = song[0];
        console.log(song.length);

    }
    $("#d1").fadeOut(500);
    $("#d2").fadeOut(500);

    if(n==1){

        Wsongs.push(d1.src);

    }
    else{

        Wsongs.push(d2.src);

    }
    rounds++;
    $("#d1").fadeIn(300);
    $("#d2").fadeIn(300);
    setTimeout(function() {

        randnumber = Math.floor(Math.random() * (song.length));
        d1.src = song[randnumber];
        song.splice(song.indexOf(d1.src),1);
        temp++;
        randnumber = Math.floor(Math.random() * (song.length));
        d2.src = song[randnumber];
        song.splice(song.indexOf(d2.src),1);
        temp++;
        
    }, 400);


    rounds_p.innerHTML = rounds + "/" + roundsend;
}
function StartGame(){
    
    $(".startingscreen").fadeOut(1000);
    setInterval(function(){

        $(".startingscreen").hide();
        $(".hide").fadeIn(1000);

    },1000)
    setInterval(function(){

        $(".hide").show(1000);

    },1000)
}
function EndGame(){

    
    $(".hide").fadeOut(1000);
    setInterval(function(){

        $(".hide").hide();
        $(".endscreen").fadeIn(1000);

    },1000)
    setInterval(function(){

        $(".endscreen").show(1000);

    },1000)

}
function Refresh(){

    location.reload();
}